-- ================================================================================
-- BRITISH NAVAL GUN VARIANTS - EXPANDED DATABASE
-- ================================================================================
-- Total: 38 gun variants (expanded from original 12)
-- Strategy: Match USA detail level with all historical Mark variants
-- Research: NavWeaps.com, Wikipedia
-- ================================================================================

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (501, 'Britain', '18"', '/40', 'Mark I', 1917, 149.0, 0, '18"/40 (45.7 cm) Mark I - Largest British naval gun. HMS Furious (removed before combat), General Wolfe/Lord Clive monitors. Shell: 3,320 lbs. MV: 2,270 fps (standard), 2,420 fps (supercharge). Max Range: 40,500 yds @ 45°. ROF: 1 rpm. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (502, 'Britain', '15"', '/42', 'Mark I', 1915, 100.0, 0, '15"/42 (38.1 cm) Mark I - Most successful British battleship gun. Queen Elizabeth, Hood, Courageous, Glorious. Shell: 1,938 lbs. MV: 2,450 fps (standard), 2,640 fps (supercharge). 186 guns manufactured 1912-1918. Service: 1915-1960. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (507, 'Britain', '15"', '/42', 'Mark I(N)', 1946, 100.0, 0, '15"/42 Mark I(N) - Modified Mark I for HMS Vanguard. Guns from Courageous/Glorious converted with additional armor, RP12 remote power control. Last British battleship. Shell: 1,938 lbs. MV: 2,640 fps. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (503, 'Britain', '16"', '/45', 'Mark I', 1927, 108.0, 0, '16"/45 (40.6 cm) Mark I - Only British triple 16" turrets. Nelson, Rodney. Shell: 2,048 lbs. MV: 2,525 fps (operational, reduced from 2,700 due to barrel wear). Barrel life: 180 rounds. Famous: Rodney vs Bismarck 1941. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (504, 'Britain', '14"', '/45', 'Mark VII', 1940, 79.62, 0, '14"/45 (35.6 cm) Mark VII - King George V-class quad/twin turrets. Shell: 1,590 lbs. MV: 2,483 fps. Quad turret: 1,582 tons. ROF: 2 rpm. Barrel life: 340 rounds. Known reliability issues. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (505, 'Britain', '13.5"', '/45', 'Mark V(L)', 1912, 76.0, 0, '13.5"/45 (34.3 cm) Mark V(L) - "Light" variant. WWI superdreadnoughts. Orion, Iron Duke, Lion. Shell: 1,250 lbs. MV: 2,582 fps. Fought at Jutland 1916. ROF: 1.5 rpm. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (508, 'Britain', '13.5"', '/45', 'Mark V(H)', 1912, 76.0, 0, '13.5"/45 Mark V(H) - "Heavy" variant. Same ships as Mark V(L) but heavier shell. Shell: 1,400 lbs. MV: 2,491 fps. 206 guns total. Jutland 1916. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (506, 'Britain', '12"', '/45', 'Mark X', 1906, 58.0, 0, '12"/45 (30.5 cm) Mark X - HMS Dreadnought revolutionary design. Shell: 850 lbs. MV: 2,725 fps. ROF: 2 rpm. First all-big-gun battleship 1906. Last successful 12" British gun. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (509, 'Britain', '12"', '/50', 'Mark XI', 1910, 60.0, 0, '12"/50 Mark XI - Vickers design. Shell: 850 lbs. MV: 2,825 fps. Bore erosion issues, short barrel life, poor accuracy. Not entirely satisfactory. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (510, 'Britain', '12"', '/50', 'Mark XI*', 1910, 60.0, 0, '12"/50 Mark XI* - Modified Mark XI. Interchangeable with Mark XI and XII. Shell: 850 lbs. MV: 2,825 fps. Ballistic performance similar to Mark XI. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (511, 'Britain', '12"', '/50', 'Mark XII', 1910, 60.0, 0, '12"/50 Mark XII - Interchangeable with Mark XI/XI*. Shell: 850 lbs. MV: 2,825 fps. Inconsistent cordite burning affected accuracy. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (520, 'Britain', '8"', '/50', 'Mark VIII', 1928, 16.0, 0, '8"/50 (20.3 cm) Mark VIII - County-class heavy cruisers. Shell: 256 lbs. MV: 2,725 fps. ROF: 5 rpm design, 3-4 sustained. Barrel life: 550 EFC. Mark I turrets +70° DP, Mark II +50°. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (512, 'Britain', '6"', '/45', 'Mark VII', 1899, 7.0, 0, '6"/45 (15.2 cm) Mark VII - Iron Duke, Tiger, monitors. Wire-wound construction. Breech opens right. WWI service. Shell: ~100 lbs. ROF: 5-7 rpm. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (513, 'Britain', '6"', '/45', 'Mark VIII', 1899, 7.0, 0, '6"/45 Mark VIII - Identical to Mark VII except breech opens left for twin mount left-hand gun. Iron Duke, Tiger. Wire-wound. Shell: ~100 lbs. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (514, 'Britain', '6"', '/45', 'Mark XII', 1914, 7.0, 0, '6"/45 Mark XII - WWI light cruisers, dreadnought secondary. High-velocity, wire-wound. Superseded Mark VII. Shell: 100 lbs. MV: 2,800 fps. ROF: 8 rpm. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (515, 'Britain', '6"', '/50', 'Mark XVI', 1914, 7.5, 0, '6"/50 Mark XVI - Ex-Turkish battleship Erin. Vickers guns. Shell: 100 lbs. MV: 3,000 fps (highest velocity British 6"). Designated Mark XVI in British service. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (516, 'Britain', '6"', '/50', 'Mark XXII', 1926, 7.5, 0, '6"/50 Mark XXII - Secondary armament on 1920s battleships. Superseded Mark XII on capital ships. Shell: 112 lbs. MV: 2,760 fps. ROF: 8 rpm. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (530, 'Britain', '6"', '/50', 'Mark XXIII', 1939, 7.0, 0, '6"/50 (15.2 cm) Mark XXIII - Crown Colony light cruisers. Shell: 112 lbs. MV: 2,760 fps. ROF: 8 rpm. Triple turrets: center gun offset 30". Barrel life: 1,100-2,200 EFC. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (540, 'Britain', '5.25"', '/50', 'QF Mark I', 1940, 5.0, 0, '5.25"/50 (13.3 cm) QF Mark I - KGV secondary, Dido-class main. Shell: 80 lbs (largest hand-loaded). MV: 2,672 fps. ROF: 12 rpm design, 7-8 sustained. Dual-purpose +70°. 267 guns. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (541, 'Britain', '5.25"', '/50', 'QF Mark II', 1942, 5.0, 0, '5.25"/50 QF Mark II - Improved Mark I. Dido-class later ships. Shell: 80 lbs. MV: 2,672 fps. ROF: 12 rpm. Dual-purpose. Cramped, slow traverse issues persisted. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (545, 'Britain', '4.7"', '/45', 'QF Mark IX', 1916, 3.5, 0, '4.7"/45 (12 cm) QF Mark IX - Standard WWI/WWII destroyers. A-class to R-class. Single mounts CP Mk XIV. Shell: 50 lbs. MV: 2,650 fps. ROF: 12 rpm sustained, 18 burst. 742 guns. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (546, 'Britain', '4.7"', '/45', 'QF Mark XII', 1936, 3.6, 0, '4.7"/45 QF Mark XII - Twin mountings. Tribal, J, K, N classes. CP Mk XIX mounts. Slightly heavier/longer than Mark IX. Shell: 50 lbs. MV: 2,650 fps. 372 guns. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (547, 'Britain', '4.7"', '/50', 'QF Mark XI', 1943, 4.0, 0, '4.7"/50 QF Mark XI - Battle-class destroyers. Longer barrel, higher velocity. Shell: 50 lbs. MV: 2,800 fps. ROF: 15 rpm. Improved AA elevation 55°. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (517, 'Britain', '4.5"', '/45', 'QF Mark I', 1938, 4.0, 0, '4.5"/45 (11.4 cm) QF Mark I - Emergency Programme destroyers. Twin mounts. Shell: 55 lbs. MV: 2,400 fps. ROF: 12-15 rpm. ~800 guns total (all 4.5" marks). Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (518, 'Britain', '4.5"', '/45', 'QF Mark II', 1939, 4.0, 0, '4.5"/45 QF Mark II - Army AAA weapon. Not mounted afloat. 16.5 ton mountings. Elevation 80°. Similar to naval guns. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (519, 'Britain', '4.5"', '/45', 'QF Mark III', 1940, 4.0, 0, '4.5"/45 QF Mark III - Interchangeable with Mark I, different firing mechanism. Twin mounts. Shell: 55 lbs. MV: 2,400 fps. Fleet destroyers. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (521, 'Britain', '4.5"', '/45', 'QF Mark IV', 1942, 4.0, 0, '4.5"/45 QF Mark IV - Modified for standard 4.7" mountings, separate ammunition. Shell: 55 lbs. MV: 2,400 fps. Small warships. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (550, 'Britain', '4.5"', '/45', 'QF Mark V', 1950, 4.0, 0, '4.5"/45 (11.4 cm) QF Mark V - Post-war automated. Daring-class. RPC, automatic ramming. Shell: 55 lbs. MV: 2,449 fps. ROF: 24 rpm power, 12-14 hand. ~300 guns. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (551, 'Britain', '4.5"', '/45', 'QF Mark VI', 1955, 4.0, 0, '4.5"/45 QF Mark VI - 1950s nomenclature change. Mark V gun on BD Mark VI mounting. Shell: 55 lbs. MV: 2,449 fps. ROF: 24 rpm. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (522, 'Britain', '4"', '/45', 'QF Mark V', 1911, 2.0, 0, '4"/45 (10.2 cm) QF Mark V - Primary AA gun. Shell: 31 lbs. MV: 2,660 fps. ROF: 15-20 rpm. Fixed ammunition. Superseded by Mark XVI in 1930s. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (555, 'Britain', '4"', '/45', 'QF Mark XVI', 1936, 2.0, 0, '4"/45 (10.2 cm) QF Mark XVI - Standard WWII AA/DP gun. Shell: 35 lbs (increased from 31). MV: 2,660 fps. ROF: 15-20 rpm. 2,555+ produced. HMS Carlisle: 11 aircraft kills. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (556, 'Britain', '4"', '/45', 'QF Mark XVI*', 1940, 2.0, 0, '4"/45 QF Mark XVI* - Autofretted loose barrel variant. Sealing collar at jacket front. Shell: 35 lbs. MV: 2,660 fps. Included in 2,555+ production. Source: NavWeaps');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (557, 'Britain', '4"', '/45', 'QF Mark XIX', 1942, 2.0, 0, '4"/45 QF Mark XIX - Heavier projectile variant. Shell: 35 lbs (increased from 31 lbs of Mk IX). MV: 2,660 fps. Separate model from Mark XVI. Source: Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (558, 'Britain', '4"', '/45', 'QF Mark XXI', 1943, 1.9, 0, '4"/45 QF Mark XXI - Lightweight autofretted monobloc barrel, removable breech ring. Shell: 35 lbs. MV: 2,660 fps. 238 guns produced. Source: NavWeaps, Wikipedia');

INSERT INTO Guns (Gun_ID, Country, Caliber, Length, Mark_Designation, Year_Introduced, Weight, Modded, Notes)
VALUES (559, 'Britain', '4"', '/40', 'QF Mark XII', 1918, 1.8, 0, '4"/40 QF Mark XII - Submarine deck gun WWI. Shell: 31 lbs. MV: 2,400 fps. 60 Mark XII + 52 Mark XII* produced. Later Mark XXII for WWII subs. Source: Wikipedia');

